<?php

namespace Rbs\Bundle\CoreBundle\Repository;

use Doctrine\ORM\EntityRepository;
use Rbs\Bundle\CoreBundle\Entity\Depo;

/**
 * DepoRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class DepoRepository extends EntityRepository
{
    public function getAll()
    {
        return $this->findAll();
    }

    public function create($data)
    {
        $this->_em->persist($data);
        $this->_em->flush();
    }

    public function delete($data)
    {
        $this->_em->remove($data);
        $this->_em->flush();
    }

    public function update($data)
    {
        $this->_em->persist($data);
        $this->_em->flush();
        return $this->_em;
    }

    public function getDepoId($user)
    {
        $query = $this->createQueryBuilder('d');
        $query->join('d.users', 'u');
        $query->select('d.id');
        $query->where('u.id = :user');
        $query->setParameter('user', $user);

        return $query->getQuery()->getResult();
    }

    public function getDepoForCurrentUser($user)
    {
        $query = $this->createQueryBuilder('d');
        $query->join('d.users', 'u');
        $query->where('u.id = :user');
        $query->setMaxResults(1);
        $query->setParameter('user', $user->getId());

        return $query->getQuery()->getResult();
    }

    public function getAllActiveDepo()
    {
        $query = $this->createQueryBuilder('d');
        $query->select('d.name');
        $query->where('d.deletedAt IS NULL');
        $query->andWhere('d.usedInTransport = 1');
        $query->orderBy('d.name', 'ASC');

        return $query->getQuery()->getResult();
    }
    public function getAllActiveDepotForChick()
    {
        $query = $this->createQueryBuilder('d');
        $query->where('d.deletedAt IS NULL');
        $query->andWhere('d.depotType = :type');
        $query->setParameter('type',Depo::DEPOT_TYPE_CHICK);
        $query->orderBy('d.name', 'ASC');

        return $query->getQuery()->getResult();
    }
    public function getAllActiveDepotForFeed()
    {
        $query = $this->createQueryBuilder('d');
        $query->where('d.deletedAt IS NULL');
        $query->andWhere('d.depotType = :feed or d.depotType IS NULL');
        $query->setParameter('feed',Depo::DEPOT_TYPE_FEED);
        $query->orderBy('d.name', 'ASC');

        return $query->getQuery()->getResult();
    }
    public function getActiveDepotForFeed($data)
    {
        $query = $this->createQueryBuilder('d');
        $query->where('d.deletedAt IS NULL');
        $query->andWhere('d.depotType = :feed or d.depotType IS NULL');
        $query->setParameter('feed',Depo::DEPOT_TYPE_FEED);
        if (!empty($data['depo'])) {
            $this->handleSearchByDepot($data['depo'], $query);
        }
        $query->orderBy('d.name', 'ASC');

        return $query->getQuery()->getResult();
    }

    protected function handleSearchByDepot($depot, $query)
    {
        if (!empty($depot)) {
            $query->andWhere('d.id = :depot');
            $query->setParameter('depot', $depot);
        }
    }
}
