    protected function handleSearchByTwoDate($query, $startDate, $endDate)
    {
        if (!empty($startDate) && !empty($endDate)) {
            $query->andWhere('p.depositDate >= :startDate');
            $query->andWhere('p.depositDate <= :endDate');
            $query->setParameter('startDate', $startDate.' 00:00:00');
            $query->setParameter('endDate', $endDate.' 23:59:59');
        } elseif (!empty($startDate) && empty($endDate)){
            $query->andWhere('p.depositDate >= :startDate');
            $query->setParameter('startDate', $startDate.' 00:00:00');
        }
    }

    public function getPaymentsBy($params){
        $qb = $this->createQueryBuilder('p');
        $qb->select('p');

        if (isset($params['orders'])) {
            $orderId = is_array($params['orders']) ? $params['orders'] : array($params['orders']);
            $qb->join('p.orders', 'o');
            $qb->andWhere($qb->expr()->in('o.id', $orderId));
        }

        if (isset($params['verified'])) {
            $qb->andWhere($qb->expr()->eq('p.verified', $params['verified']));
        }

        if (isset($params['transactionType'])) {
            $qb->andWhere($qb->expr()->eq('p.transactionType', '?1'));
            $qb->setParameter('1', $params['transactionType']);
        }

        return $qb->getQuery()->getResult();
    }

    public function createDeliveredProductValue(Delivery $delivery)
    {
        $this->addPaymentByDeliveryItemExtractedSummary(
            $this->extractDeliveryItemSummary($delivery), $delivery
        );
    }

    public function createDeliveredProductValueForSingleChickOrder(Delivery $delivery, Order $order)
    {
        $this->addPaymentByDeliveryItemSingleOrder($this->extractOrderItemSummary($delivery, $order), $delivery, $order);
    }

    public function updateDeliveredProductValueForSingleChickOrder(Delivery $delivery, Order $order)
    {
        $this->updatePaymentByDeliveryItemSingleOrder($this->extractOrderItemSummary($delivery, $order), $delivery, $order);
    }

    public function updateDeliveredProductValue(Delivery $delivery)
    {
        $this->updatePaymentByDeliveryItemExtractedSummary(
            $this->extractDeliveryItemSummary($delivery)
        );
    }

    private function extractDeliveryItemSummary(Delivery $delivery)
    {
        $data = [];

        /** @var DeliveryItem $deliveryItem */
        foreach ($delivery->getDeliveryItems() as $deliveryItem) {
            $order = $deliveryItem->getOrder();
            $itemPrice = $deliveryItem->getOrderItem()->getPrice();
            $itemDamageQty = $deliveryItem->getOrderItem()->getDamageQuantity();
            $itemQty = $deliveryItem->getQty();
            $itemQty = $itemQty - $itemDamageQty;
            $amount = $itemPrice * $itemQty;

            if (!array_key_exists($order->getId(), $data)) {
                $data[$order->getId()] = [];
            }

            $item = $deliveryItem->getOrderItem()->getItem();
            $data[$order->getId()][$item->getId()] = [
                'name' => $item->getName(),
                'qty' => $itemQty,
                'price' => $itemPrice,
                'amount' => $amount,
                'unit' => $item->getItemUnit(),
                'deliveryId' => $deliveryItem->getDelivery()->getId(),
            ];
        }

        return $data;
    }

    private function addPaymentByDeliveryItemExtractedSummary($data, Delivery $delivery)
    {
        foreach ($data as $orderId => $deliveryItemSummaries) {
            $order = $this->_em->getRepository('RbsSalesBundle:Order')->find($orderId);
            $total = array_sum(array_column($data[$orderId], 'amount'));
            $quantity = array_sum(array_column($data[$orderId], 'qty'));
            $payment = new Payment();
            $payment->setAgent($order->getAgent());
            $payment->setAmount($total);
            $payment->setQuantity($quantity);
            $payment->setDepositedAmount($total);
            $payment->setPaymentMethod(Payment::PAYMENT_METHOD_BANK);
            $payment->setRemark(json_encode($deliveryItemSummaries));
//            $payment->setDepositDate(date("Y-m-d"));
            $payment->setTransactionType(Payment::DR);
            $payment->setVerified(true);
            $payment->setRefDeliveryId($delivery->getId());
            $payment->addOrder($order);

            $this->_em->persist($payment);
            $this->_em->flush();
        }
    }



    private function extractOrderItemSummary(Delivery $delivery, Order $order)
    {
        $data = array();

        /** @var OrderItem $orderItem */
        foreach ($order->getOrderItems() as $orderItem) {
//            $order = $orderItem->getOrder();
            $itemPrice = $orderItem->getPrice();
            $itemDamageQty = $orderItem->getDamageQuantity();
            $itemQty = $orderItem->getQuantity();
            $itemQty = $itemQty - $itemDamageQty;
            $amount = $itemPrice * $itemQty;

            if (!array_key_exists($order->getId(), $data)) {
                $data[$order->getId()] = array();
            }

            $item = $orderItem->getItem();
            $data[$order->getId()][$item->getId()] = array(
                'name' => $item->getName(),
                'qty' => $itemQty,
                'price' => $itemPrice,
                'amount' => $amount,
                'unit' => $item->getItemUnit(),
                'deliveryId' => $delivery->getId(),
            );
        }

        return $data;
    }

    private function addPaymentByDeliveryItemSingleOrder($data, Delivery $delivery, Order $order)
    {
        foreach ($data as $deliveryItemSummaries) {
//            $order = $this->_em->getRepository('RbsSalesBundle:Order')->find($orderId);
            $total = array_sum(array_column($data[$order->getId()], 'amount'));
            $quantity = array_sum(array_column($data[$order->getId()], 'qty'));
            $payment = new Payment();
            $payment->setAgent($order->getAgent());
            $payment->setAmount($total);
            $payment->setQuantity($quantity);
            $payment->setDepositedAmount($total);
            $payment->setPaymentMethod(Payment::PAYMENT_METHOD_BANK);
            $payment->setRemark(json_encode($deliveryItemSummaries));
//            $payment->setDepositDate(date("Y-m-d"));
            $payment->setTransactionType(Payment::DR);
            $payment->setVerified(true);
            $payment->setRefDeliveryId($delivery->getId());
            $payment->addOrder($order);

            $this->_em->persist($payment);
            $this->_em->flush();
        }
    }

    private function updatePaymentByDeliveryItemSingleOrder($data, Delivery $delivery, Order $order)
    {
        foreach ($data as $deliveryItemSummaries) {
//            $order = $this->_em->getRepository('RbsSalesBundle:Order')->find($orderId);
            $total = array_sum(array_column($data[$order->getId()], 'amount'));
            $quantity = array_sum(array_column($data[$order->getId()], 'qty'));
            $payments = $order->getPayments();
            /** @var Payment $payment */
            foreach ($payments as $payment){
                if($payment->getRefDeliveryId()==$delivery->getId() && $payment->getTransactionType()==Payment::DR){
                    $payment->setAgent($order->getAgent());
                    $payment->setAmount($total);
                    $payment->setQuantity($quantity);
                    $payment->setDepositedAmount($total);
                    $payment->setPaymentMethod(Payment::PAYMENT_METHOD_BANK);
                    $payment->setRemark(json_encode($deliveryItemSummaries));
//            $payment->setDepositDate(date("Y-m-d"));
                    $payment->setTransactionType(Payment::DR);
                    $payment->setVerified(true);
                    $payment->addOrder($order);
                    $this->_em->persist($payment);
                    $this->_em->flush();
                }
            }
        }
    }

    private function updatePaymentByDeliveryItemExtractedSummary($data)
    {
        foreach ($data as $orderId => $deliveryItemSummaries) {
            $order = $this->_em->getRepository('RbsSalesBundle:Order')->find($orderId);
            $total = array_sum(array_column($data[$orderId], 'amount'));
            $quantity = array_sum(array_column($data[$orderId], 'qty'));
            $deliveryId = array_sum(array_column($data[$orderId], 'deliveryId'));
            $payments = $order->getPayments();
            /** @var Payment $payment */
            foreach ($payments as $payment){
                if($payment->getRefDeliveryId()==$deliveryId){
                    $payment->setAgent($order->getAgent());
                    $payment->setAmount($total);
                    $payment->setQuantity($quantity);
                    $payment->setDepositedAmount($total);
                    $payment->setPaymentMethod(Payment::PAYMENT_METHOD_BANK);
                    $payment->setRemark(json_encode($deliveryItemSummaries));
//            $payment->setDepositDate(date("Y-m-d"));
                    $payment->setTransactionType(Payment::DR);
                    $payment->setVerified(true);
                    $payment->addOrder($order);
                    $this->_em->persist($payment);
                    $this->_em->flush();
                }
            }
        }
    }

    public function getPaymentReportData($type, $startDate, $endDate, $agent = null)
    {
        $startDateTime = new \DateTime($startDate);
        $interval = date_diff($startDateTime, new \DateTime($endDate));
        $diff = (int)$interval->format($type == 'monthly' ? '%m' : '%a');
        $intervalType = $type == 'monthly' ? '+1 month' : '+1 day';

        $reportData = [];
        for ($i = 0; $i <= $diff; $i++) {
            $tempDate = $i == 0 ? $startDateTime : $startDateTime->modify($intervalType);
            $key = $type == 'monthly' ? $tempDate->format('F Y') : $tempDate->format('d-m-Y');
            $reportData[$key] = $this->getPaymentReportDateOf(
                $type,
                $tempDate,
                $agent
            );
        }

        return array_filter($reportData);
    }

    public function getPaymentReportDateOf($type, \DateTime $date, $agent)
    {
        if ($type == 'monthly') {
            $start = $date->format('Y-m-1 00:00:00');
            $end = $date->format('Y-m-t 23:59:59');
        } else {
            $start = $date->format('Y-m-d 00:00:00');
            $end = $date->format('Y-m-d 23:59:59');
        }

        $qb = $this->createQueryBuilder('p');
        $qb->join('p.agent', 'agent');
        $qb->join('agent.user', 'user');
        $qb->join('user.profile', 'profile');
        $qb->leftJoin('p.agentBankBranch', 'abb');

        $qb->select('p.createdAt, profile.fullName, agent.agentID, p.agentBank, p.agentBranch, p.amount, abb.bank, abb.branch');

        $qb->where('p.verified = :verified')->setParameter('verified', true);
        $qb->andWhere('p.transactionType = :transactionType')->setParameter('transactionType', Payment::CR);

        $qb->andWhere('p.createdAt BETWEEN :start AND :end');
        $qb->setParameter('start', $start);
        $qb->setParameter('end', $end);

        if ($agent) {
            $qb->andWhere('p.agent = :agent')->setParameter('agent', $agent);
        }

        $result = $qb->getQuery()->getResult();

        if (empty($result)) {
            return [];
        }
        return ['data' => $qb->getQuery()->getResult(), 'start' => $start, 'end' => $end];
    }

    public function getPaymentsByDate($requestDate)
    {
        $date = $requestDate?date('Y-m-d', strtotime($requestDate)): date('Y-m-d', strtotime('now'));
        $start= $date.' 00:00:00';
        $end = $date.' 23:59:59';

        $qb = $this->createQueryBuilder('p');
        $qb->join('p.agent', 'agent');
        $qb->join('agent.user', 'user');
        $qb->join('user.profile', 'profile');
        $qb->join('p.bank', 'b');
        $qb->join('p.branch', 'br');

        $qb->select('p.createdAt, p.depositDate, profile.fullName as agentName, agent.agentCodeForDatatable as agentId, p.amount, b.name as bankName, br.name as branchName, br.branchCode as branchCode');

        $qb->where('p.verified = :verified')->setParameter('verified', true);
        $qb->andWhere('p.transactionType = :transactionType')->setParameter('transactionType', Payment::CR);

        $qb->andWhere('p.createdAt BETWEEN :start AND :end');
        $qb->setParameter('start', $start);
        $qb->setParameter('end', $end);

        $results = $qb->getQuery()->getResult();
        $returnArray=array();
        if($results){
            foreach ($results as $result){
                $returnArray[]= array(
                    'agentId'=> $result['agentId'],
                    'agentName'=> $result['agentName'],
                    'createdAt'=> $result['createdAt']?$result['createdAt']->format('d-m-Y'):'',
                    'depositDate'=> $result['depositDate']?$result['depositDate']->format('d-m-Y'):'',
                    'amount'=> $result['amount'],
                    'bankName'=> $result['bankName'],
                    'branchName'=> $result['branchName'],
                );
            }
        }

        return $returnArray;

    }
